import React, { useEffect, useState, createContext, useContext, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from 'axios';

const UserContext = createContext();
const API = axios.create({ baseURL: 'http://localhost:3001', withCredentials: true });

function useUser() { return useContext(UserContext); }

function Logo() {
  // Placeholder logo: vintage key emoji
  return (
    <span style={{ fontSize: '2.2rem', marginRight: 12, verticalAlign: 'middle' }} role="img" aria-label="Heirloom Key">üóùÔ∏è</span>
  );
}

function Navbar() {
  const { user, logout } = useUser();
  return (
    <nav style={{ background: '#7e5a75', padding: '1rem 2rem', color: '#f5f1ec', display: 'flex', justifyContent: 'space-between', alignItems: 'center', boxShadow: '0 2px 8px #3c4c7333' }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Logo />
        <Link to="/" style={{ color: '#f5f1ec', fontWeight: 700, fontSize: '1.7rem', textDecoration: 'none', letterSpacing: 1 }}>Heirloom Speech</Link>
      </div>
      <div>
        <Link to="/analyze" style={{ color: '#f5f1ec', marginRight: 20 }}>Analyze</Link>
        {user && user.role === 'admin' && <Link to="/admin" style={{ color: '#f5f1ec', marginRight: 20 }}>Admin</Link>}
        {user ? (
          <button onClick={logout} style={{ background: 'none', border: 'none', color: '#f5f1ec', cursor: 'pointer', fontSize: '1rem' }}>Logout</button>
        ) : (
          <Link to="/login" style={{ color: '#f5f1ec' }}>Login</Link>
        )}
      </div>
    </nav>
  );
}

function Landing() {
  // Waitlist state
  const [email, setEmail] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState('');

  const handleWaitlistSubmit = (e) => {
    e.preventDefault();
    setError('');
    if (!email.match(/^[^@\s]+@[^@\s]+\.[^@\s]+$/)) {
      setError('Please enter a valid email address.');
      return;
    }
    setSubmitted(true);
    setEmail('');
  };

  return (
    <div style={{
      minHeight: '90vh',
      background: 'linear-gradient(135deg, #f5f1ec 60%, #c38d94 100%)',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '3rem 1rem 2rem 1rem',
    }}>
      {/* Hero + Waitlist + Features */}
      <div style={{
        background: '#fff',
        borderRadius: 24,
        boxShadow: '0 2px 16px #3c4c7333',
        padding: '2.5rem 2rem',
        maxWidth: 540,
        width: '100%',
        textAlign: 'center',
        border: '2px solid #c38d94',
        marginBottom: 32,
      }}>
        <Logo />
        <h1 style={{ color: '#7e5a75', fontSize: '2.5rem', margin: '0.5rem 0 1rem 0', fontFamily: 'serif', letterSpacing: 1 }}>Heirloom Speech</h1>
        <p style={{ color: '#3e3e3e', fontSize: '1.15rem', marginBottom: 30, fontFamily: 'serif', lineHeight: 1.6 }}>
          <span style={{ fontWeight: 600 }}>Reconnect with your roots, one conversation at a time.</span><br/>
          Heirloom Speech is a gentle, AI-powered companion for heritage speakers‚Äîhelping you rediscover, practice, and cherish your language in a safe, supportive space.
        </p>
        <Link to="/analyze" style={{ background: '#c38d94', color: '#3e3e3e', padding: '1rem 2.2rem', borderRadius: 14, fontWeight: 700, textDecoration: 'none', fontSize: '1.1rem', boxShadow: '0 2px 8px #c38d9433', marginBottom: 18, display: 'inline-block' }}>Start a Conversation</Link>
        <div style={{ margin: '2rem 0 0.5rem 0', color: '#7e5a75', fontWeight: 600, fontSize: '1.1rem' }}>Join the Waitlist</div>
        <form onSubmit={handleWaitlistSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: 8 }}>
          {submitted ? (
            <div style={{ color: '#3e3e3e', background: '#c38d94', padding: '0.7rem 1.2rem', borderRadius: 10, fontWeight: 600, marginBottom: 8 }}>Thank you for joining the waitlist!</div>
          ) : (
            <>
              <input
                type="email"
                value={email}
                onChange={e => setEmail(e.target.value)}
                placeholder="Your email address"
                style={{
                  padding: '0.7rem 1rem',
                  borderRadius: 8,
                  border: '1.5px solid #c38d94',
                  marginBottom: 8,
                  width: 220,
                  fontSize: '1rem',
                  fontFamily: 'serif',
                  background: '#f5f1ec',
                  color: '#3e3e3e',
                }}
                required
              />
              <button type="submit" style={{
                background: '#7e5a75',
                color: '#fff',
                border: 'none',
                borderRadius: 8,
                padding: '0.7rem 1.5rem',
                fontWeight: 600,
                fontSize: '1rem',
                cursor: 'pointer',
                boxShadow: '0 2px 8px #7e5a7522',
                marginBottom: 4,
              }}>Notify Me</button>
            </>
          )}
          {error && <div style={{ color: '#b04a2b', fontSize: '0.95rem', marginTop: 2 }}>{error}</div>}
        </form>
      </div>

      {/* Subtle Divider */}
      <div style={{ width: '100%', maxWidth: 900, margin: '32px 0 0 0', height: 1, background: 'linear-gradient(90deg, #f5f1ec 0%, #c38d94 50%, #f5f1ec 100%)', opacity: 0.3 }} />

      {/* Feature Cards */}
      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        gap: 24,
        justifyContent: 'center',
        marginTop: 32,
        maxWidth: 900,
        width: '100%',
      }}>
        <FeatureCard icon="ü§ù" title="Conversational Practice" desc="Chat naturally with AI to build confidence and fluency in your heritage language." />
        <FeatureCard icon="üå±" title="Gentle Guidance" desc="Supportive, non-judgmental feedback to help you grow at your own pace." />
        <FeatureCard icon="üóùÔ∏è" title="Cultural Connection" desc="Rediscover stories, sayings, and traditions that make your language unique." />
      </div>

      {/* Subtle Divider */}
      <div style={{ width: '100%', maxWidth: 900, margin: '40px 0 0 0', height: 1, background: 'linear-gradient(90deg, #c38d94 0%, #f5f1ec 100%)', opacity: 0.18 }} />

      {/* Quotes/Testimonials Section */}
      <div style={{
