import React, { useEffect, useState, createContext, useContext, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from 'axios';

const UserContext = createContext();
const API = axios.create({ baseURL: 'http://localhost:3001', withCredentials: true });

function useUser() { return useContext(UserContext); }

function Logo() {
  // Placeholder logo: vintage key emoji
  return (
    <span style={{ fontSize: '2.2rem', marginRight: 12, verticalAlign: 'middle' }} role="img" aria-label="Heirloom Key">üóùÔ∏è</span>
  );
}

function Navbar() {
  const { user, logout } = useUser();
  return (
    <nav style={{ background: '#7c5c3e', padding: '1rem 2rem', color: '#e2c799', display: 'flex', justifyContent: 'space-between', alignItems: 'center', boxShadow: '0 2px 8px #e2c79933' }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Logo />
        <Link to="/" style={{ color: '#e2c799', fontWeight: 700, fontSize: '1.7rem', textDecoration: 'none', letterSpacing: 1 }}>Heirloom Speech</Link>
      </div>
      <div>
        <Link to="/analyze" style={{ color: '#f3e7d4', marginRight: 20 }}>Analyze</Link>
        {user && user.role === 'admin' && <Link to="/admin" style={{ color: '#f3e7d4', marginRight: 20 }}>Admin</Link>}
        {user ? (
          <button onClick={logout} style={{ background: 'none', border: 'none', color: '#e2c799', cursor: 'pointer', fontSize: '1rem' }}>Logout</button>
        ) : (
          <Link to="/login" style={{ color: '#e2c799' }}>Login</Link>
        )}
      </div>
    </nav>
  );
}

function Landing() {
  // Waitlist state
  const [email, setEmail] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState('');

  const handleWaitlistSubmit = (e) => {
    e.preventDefault();
    setError('');
    if (!email.match(/^[^@\s]+@[^@\s]+\.[^@\s]+$/)) {
      setError('Please enter a valid email address.');
      return;
    }
    setSubmitted(true);
    setEmail('');
  };

  return (
    <div style={{
      minHeight: '90vh',
      background: 'linear-gradient(135deg, #f7f3ef 60%, #e2c799 100%)',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '3rem 1rem 2rem 1rem',
    }}>
      <div style={{
        background: '#fff',
        borderRadius: 24,
        boxShadow: '0 2px 16px #e2c79933',
        padding: '2.5rem 2rem',
        maxWidth: 540,
        width: '100%',
        textAlign: 'center',
        border: '1.5px solid #e2c799',
        marginBottom: 32,
      }}>
        <Logo />
        <h1 style={{ color: '#7c5c3e', fontSize: '2.5rem', margin: '0.5rem 0 1rem 0', fontFamily: 'serif', letterSpacing: 1 }}>Heirloom Speech</h1>
        <p style={{ color: '#5c4322', fontSize: '1.15rem', marginBottom: 30, fontFamily: 'serif', lineHeight: 1.6 }}>
          <span style={{ fontWeight: 600 }}>Reconnect with your roots, one conversation at a time.</span><br/>
          Heirloom Speech is a gentle, AI-powered companion for heritage speakers‚Äîhelping you rediscover, practice, and cherish your language in a safe, supportive space.
        </p>
        <Link to="/analyze" style={{ background: '#e2c799', color: '#5c4322', padding: '1rem 2.2rem', borderRadius: 14, fontWeight: 700, textDecoration: 'none', fontSize: '1.1rem', boxShadow: '0 2px 8px #e2c79933', marginBottom: 18, display: 'inline-block' }}>Start a Conversation</Link>
        <div style={{ margin: '2rem 0 0.5rem 0', color: '#7c5c3e', fontWeight: 600, fontSize: '1.1rem' }}>Join the Waitlist</div>
        <form onSubmit={handleWaitlistSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: 8 }}>
          {submitted ? (
            <div style={{ color: '#5c4322', background: '#e2c799', padding: '0.7rem 1.2rem', borderRadius: 10, fontWeight: 600, marginBottom: 8 }}>Thank you for joining the waitlist!</div>
          ) : (
            <>
              <input
                type="email"
                value={email}
                onChange={e => setEmail(e.target.value)}
                placeholder="Your email address"
                style={{
                  padding: '0.7rem 1rem',
                  borderRadius: 8,
                  border: '1.5px solid #e2c799',
                  marginBottom: 8,
                  width: 220,
                  fontSize: '1rem',
                  fontFamily: 'serif',
                  background: '#f7f3ef',
                  color: '#7c5c3e',
                }}
                required
              />
              <button type="submit" style={{
                background: '#7c5c3e',
                color: '#fff',
                border: 'none',
                borderRadius: 8,
                padding: '0.7rem 1.5rem',
                fontWeight: 600,
                fontSize: '1rem',
                cursor: 'pointer',
                boxShadow: '0 2px 8px #7c5c3e22',
                marginBottom: 4,
              }}>Notify Me</button>
            </>
          )}
          {error && <div style={{ color: '#b04a2b', fontSize: '0.95rem', marginTop: 2 }}>{error}</div>}
        </form>
      </div>
      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        gap: 24,
        justifyContent: 'center',
        marginTop: 8,
        maxWidth: 900,
        width: '100%',
      }}>
        <FeatureCard icon="ü§ù" title="Conversational Practice" desc="Chat naturally with AI to build confidence and fluency in your heritage language." />
        <FeatureCard icon="üå±" title="Gentle Guidance" desc="Supportive, non-judgmental feedback to help you grow at your own pace." />
        <FeatureCard icon="üóùÔ∏è" title="Cultural Connection" desc="Rediscover stories, sayings, and traditions that make your language unique." />
      </div>
    </div>
  );
}

function FeatureCard({ icon, title, desc }) {
  return (
    <div style={{
      background: '#fff',
      border: '1.5px solid #e2c799',
      borderRadius: 16,
      boxShadow: '0 2px 12px #e2c79922',
      padding: '1.2rem 1.1rem',
      minWidth: 220,
      maxWidth: 260,
      flex: 1,
      textAlign: 'center',
      fontFamily: 'serif',
    }}>
      <div style={{ fontSize: '2.1rem', marginBottom: 8 }}>{icon}</div>
      <div style={{ color: '#7c5c3e', fontWeight: 700, fontSize: '1.15rem', marginBottom: 6 }}>{title}</div>
      <div style={{ color: '#5c4322', fontSize: '1rem' }}>{desc}</div>
    </div>
  );
}

// Add localStorage persistence for chatHistory
function usePersistentChatHistory(user) {
  const [chatHistory, setChatHistory] = useState(() => {
    if (!user) {
      const saved = localStorage.getItem('chatHistory');
      return saved ? JSON.parse(saved) : [];
    }
    return [];
  });

  useEffect(() => {
    if (!user) {
      localStorage.setItem('chatHistory', JSON.stringify(chatHistory));
    } else {
      localStorage.removeItem('chatHistory');
    }
  }, [chatHistory, user]);

  return [chatHistory, setChatHistory];
}

function Analyze() {
  const { user } = useUser();
  const [isRecording, setIsRecording] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [feedback, setFeedback] = useState('');
  const [isLoadingFeedback, setIsLoadingFeedback] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const [showSavePrompt, setShowSavePrompt] = useState(false);
  const [chatHistory, setChatHistory] = usePersistentChatHistory(user);

  // Offer to save session when user logs in
  useEffect(() => {
    if (user && localStorage.getItem('chatHistory')) {
      setShowSavePrompt(true);
    }
  }, [user]);

  const saveSessionToBackend = async () => {
    try {
      await API.post('/api/save-session', { chatHistory });
      setShowSavePrompt(false);
      localStorage.removeItem('chatHistory');
      alert('Session saved to your account!');
    } catch (e) {
      alert('Failed to save session.');
    }
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        await sendAudioToBackend(audioBlob);
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Error starting recording:', error);
      alert('Error accessing microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
    }
  };

  const sendAudioToBackend = async (audioBlob) => {
    setIsProcessing(true);

    // Add user message to chat
    const userMessage = { sender: 'User', text: 'üé§ Recording...', timestamp: new Date() };
    setChatHistory(prev => [...prev, userMessage]);

    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'recording.wav');
      formData.append('chatHistory', JSON.stringify(chatHistory));

      const response = await API.post('/api/analyze', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      // Update user message with transcription
      const transcription = response.data.transcription || 'Speech recorded';
      setChatHistory(prev => prev.map(msg => 
        msg === userMessage ? { ...msg, text: transcription } : msg
      ));

      // Add AI response
      if (response.data.aiResponse) {
        const aiMessage = { 
          sender: 'AI', 
          text: response.data.aiResponse, 
          timestamp: new Date() 
        };
        setChatHistory(prev => [...prev, aiMessage]);
      }

      // Play TTS audio if available
      if (response.data.ttsUrl) {
        const audio = new window.Audio(`http://localhost:4000${response.data.ttsUrl}`);
        audio.play();
      }

    } catch (error) {
      console.error('Error sending audio:', error);
      const errorMessage = { 
        sender: 'System', 
        text: '‚ùå Error processing audio. Please try again.', 
        timestamp: new Date() 
      };
      setChatHistory(prev => [...prev, errorMessage]);
    } finally {
      setIsProcessing(false);
    }
  };

  const requestDetailedFeedback = async () => {
    if (chatHistory.length === 0) {
      alert('Please record some speech first.');
      return;
    }

    setIsLoadingFeedback(true);
    try {
      const response = await API.post('/api/feedback', {
        chatHistory: chatHistory
      });
      setFeedback(response.data.feedback);
    } catch (error) {
      console.error('Error requesting feedback:', error);
      setFeedback('‚ùå Error getting detailed feedback. Please try again.');
    } finally {
      setIsLoadingFeedback(false);
    }
  };

  return (
    <div style={{ 
      display: 'flex', 
      height: 'calc(100vh - 80px)', 
      background: '#f7f3ef',
      padding: '2rem'
    }}>
      <div style={{ flex: 1, background: '#fff', borderRadius: 20, marginRight: '1rem', display: 'flex', flexDirection: 'column', border: '2px solid #e2c799' }}>
        {/* Save session prompt */}
        {showSavePrompt && (
          <div style={{ background: '#fffbe6', border: '1px solid #e2c799', padding: 16, borderRadius: 8, margin: 16 }}>
            <p>You have an unsaved session. Save it to your account?</p>
            <button onClick={saveSessionToBackend} style={{ marginRight: 8 }}>Save</button>
            <button onClick={() => { setShowSavePrompt(false); localStorage.removeItem('chatHistory'); }}>Dismiss</button>
          </div>
        )}
        {/* Chat Header */}
        <div style={{ background: '#8d6748', color: '#fff', padding: '1rem', borderRadius: '18px 18px 0 0', textAlign: 'center' }}>
          <h2>üé§ Heirloom Speech Chat</h2>
        </div>

        {/* Chat Messages */}
        <div style={{ 
          flex: 1, 
          padding: '1rem', 
          overflowY: 'auto',
          display: 'flex',
          flexDirection: 'column',
          gap: '0.5rem'
        }}>
          {chatHistory.map((message, index) => (
            <div key={index} style={{
              maxWidth: '80%',
              padding: '0.75rem 1rem',
              borderRadius: 18,
              alignSelf: message.sender === 'User' ? 'flex-end' : 'flex-start',
              background: message.sender === 'User' ? '#e2c799' : 
                         message.sender === 'AI' ? '#fff' : '#f3e7d4',
              color: message.sender === 'User' ? '#5c4322' : '#8d6748',
              border: message.sender === 'AI' ? '1px solid #e2c799' : 'none',
              fontSize: '0.9rem'
            }}>
              {message.text}
            </div>
          ))}
          {isProcessing && (
            <div style={{
              alignSelf: 'center',
              padding: '0.5rem 1rem',
              background: '#f3e7d4',
              borderRadius: 12,
              color: '#8d6748',
              fontSize: '0.9rem'
            }}>
              ‚è≥ Processing your speech...
            </div>
          )}
        </div>

        {/* Recording Controls */}
        <div style={{ 
          padding: '1rem', 
          borderTop: '1px solid #e2c799',
          background: '#f7f3ef',
          borderRadius: '0 0 18px 18px',
          textAlign: 'center'
        }}>
          <button
            onClick={isRecording ? stopRecording : startRecording}
            disabled={isProcessing}
            style={{
              width: 60,
              height: 60,
              borderRadius: '50%',
              border: 'none',
              background: isRecording ? '#b08a60' : '#e2c799',
              color: isRecording ? '#fff' : '#8d6748',
              fontSize: '24px',
              cursor: isProcessing ? 'not-allowed' : 'pointer',
              transition: 'all 0.3s',
              animation: isRecording ? 'pulse 1.5s infinite' : 'none'
            }}
          >
            {isRecording ? '‚èπÔ∏è' : 'üé§'}
          </button>
          <p style={{ marginTop: '0.5rem', color: '#8d6748', fontSize: '0.9rem' }}>
            {isRecording ? 'Recording... Click to stop' : 'Click to start recording'}
          </p>
        </div>
      </div>

      {/* Feedback Section */}
      <div style={{ 
        width: 320, 
        background: '#f3e7d4', 
        borderRadius: 20,
        display: 'flex',
        flexDirection: 'column',
        border: '2px solid #e2c799'
      }}>
        {/* Feedback Header */}
        <div style={{ 
          background: '#e2c799', 
          color: '#5c4322', 
          padding: '1rem', 
          borderRadius: '18px 18px 0 0',
          textAlign: 'center'
        }}>
          <h3>üìä Detailed Analysis</h3>
        </div>

        {/* Feedback Content */}
        <div style={{ 
          flex: 1, 
          padding: '1rem',
          display: 'flex',
          flexDirection: 'column'
        }}>
          <button
            onClick={requestDetailedFeedback}
            disabled={isLoadingFeedback || chatHistory.length === 0}
            style={{
              width: '100%',
              padding: '0.75rem',
              background: '#8d6748',
              color: '#fff',
              border: 'none',
              borderRadius: 8,
              cursor: isLoadingFeedback ? 'not-allowed' : 'pointer',
              fontWeight: 600,
              marginBottom: '1rem'
            }}
          >
            {isLoadingFeedback ? '‚è≥ Processing...' : 'Request Detailed Feedback'}
          </button>

          {feedback && (
            <div style={{
              background: '#fff',
              padding: '1rem',
              borderRadius: 8,
              border: '1px solid #e2c799',
              flex: 1,
              overflowY: 'auto',
              fontSize: '0.9rem',
              lineHeight: 1.5,
              whiteSpace: 'pre-wrap'
            }}>
              {feedback}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

function Admin() {
  const { user } = useUser();
  if (!user || user.role !== 'admin') return <Navigate to="/" />;
  return <div style={{ padding: '2rem' }}>Admin Page (Edit conversational features here)</div>;
}

function Login() {
  const { setUser } = useUser();

  const handleSuccess = async (credentialResponse) => {
    try {
      // Send the credential to your backend
      const response = await API.post('/auth/google/token', {
        credential: credentialResponse.credential
      });

      if (response.data.user) {
        setUser(response.data.user);
        window.location.href = '/';
      }
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  const handleError = () => {
    console.error('Login Failed');
  };

  return (
    <div style={{ padding: '4rem', textAlign: 'center' }}>
      <h2>Login with Google</h2>
      <GoogleLogin
        onSuccess={handleSuccess}
        onError={handleError}
        useOneTap
      />
    </div>
  );
}

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    API.get('/api/user').then(res => setUser(res.data)).catch(() => setUser(null));
  }, []);

  const logout = async () => {
    await API.get('/api/logout');
    setUser(null);
    window.location.href = '/';
  };

  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      <UserContext.Provider value={{ user, setUser, logout }}>
        <Router>
          <Navbar />
          <Routes>
            <Route path="/" element={<Landing />} />
            <Route path="/analyze" element={<Analyze />} />
            <Route path="/admin" element={<Admin />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </Router>
      </UserContext.Provider>
    </GoogleOAuthProvider>
  );
}

export default App;
