import React, { useEffect, useState, createContext, useContext, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from 'axios';

const UserContext = createContext();
const API = axios.create({ baseURL: 'http://localhost:4000', withCredentials: true });

function useUser() { return useContext(UserContext); }

function Navbar() {
  const { user, logout } = useUser();
  return (
    <nav style={{ background: '#8d6748', padding: '1rem 2rem', color: '#e2c799', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
      <Link to="/" style={{ color: '#e2c799', fontWeight: 700, fontSize: '1.5rem', textDecoration: 'none' }}>Heirloom Speech</Link>
      <div>
        <Link to="/analyze" style={{ color: '#f3e7d4', marginRight: 20 }}>Analyze</Link>
        {user && user.role === 'admin' && <Link to="/admin" style={{ color: '#f3e7d4', marginRight: 20 }}>Admin</Link>}
        {user ? (
          <button onClick={logout} style={{ background: 'none', border: 'none', color: '#e2c799', cursor: 'pointer' }}>Logout</button>
        ) : (
          <Link to="/login" style={{ color: '#e2c799' }}>Login</Link>
        )}
      </div>
    </nav>
  );
}

function Landing() {
  return (
    <div style={{ padding: '4rem', textAlign: 'center', background: '#f7f3ef', minHeight: '80vh' }}>
      <h1 style={{ color: '#8d6748', fontSize: '3rem', marginBottom: 20 }}>Master Your Accent with AI</h1>
      <p style={{ color: '#5c4322', fontSize: '1.2rem', marginBottom: 30 }}>Transform your speech with our advanced AI-powered accent coaching system. Get personalized feedback on pronunciation, grammar, and naturalness in real-time.</p>
      <Link to="/analyze" style={{ background: '#e2c799', color: '#5c4322', padding: '1rem 2rem', borderRadius: 12, fontWeight: 600, textDecoration: 'none', fontSize: '1.1rem' }}>Start Analysis</Link>
    </div>
  );
}

function Analyze() {
  const [isRecording, setIsRecording] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [feedback, setFeedback] = useState('');
  const [isLoadingFeedback, setIsLoadingFeedback] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        await sendAudioToBackend(audioBlob);
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Error starting recording:', error);
      alert('Error accessing microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
    }
  };

  const sendAudioToBackend = async (audioBlob) => {
    setIsProcessing(true);

    // Add user message to chat
    const userMessage = { sender: 'User', text: '🎤 Recording...', timestamp: new Date() };
    setChatHistory(prev => [...prev, userMessage]);

    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'recording.wav');

      const response = await API.post('/api/analyze', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      // Update user message with transcription
      const transcription = response.data.transcription || 'Speech recorded';
      setChatHistory(prev => prev.map(msg => 
        msg === userMessage ? { ...msg, text: transcription } : msg
      ));

      // Add AI response
      if (response.data.aiResponse) {
        const aiMessage = { 
          sender: 'AI', 
          text: response.data.aiResponse, 
          timestamp: new Date() 
        };
        setChatHistory(prev => [...prev, aiMessage]);
      }

    } catch (error) {
      console.error('Error sending audio:', error);
      const errorMessage = { 
        sender: 'System', 
        text: '❌ Error processing audio. Please try again.', 
        timestamp: new Date() 
      };
      setChatHistory(prev => [...prev, errorMessage]);
    } finally {
      setIsProcessing(false);
    }
  };

  const requestDetailedFeedback = async () => {
    if (chatHistory.length === 0) {
      alert('Please record some speech first.');
      return;
    }

    setIsLoadingFeedback(true);
    try {
      const response = await API.post('/api/feedback', {
        chatHistory: chatHistory
      });
      setFeedback(response.data.feedback);
    } catch (error) {
      console.error('Error requesting feedback:', error);
      setFeedback('❌ Error getting detailed feedback. Please try again.');
    } finally {
      setIsLoadingFeedback(false);
    }
  };

  return (
    <div style={{ 
      display: 'flex', 
      height: 'calc(100vh - 80px)', 
      background: '#f7f3ef',
      padding: '2rem'
    }}>
      {/* Chat Section */}
      <div style={{ 
        flex: 1, 
        background: '#fff', 
        borderRadius: 20, 
        marginRight: '1rem',
        display: 'flex',
        flexDirection: 'column',
        border: '2px solid #e2c799'
      }}>
        {/* Chat Header */}
        <div style={{ 
          background: '#8d6748', 
          color: '#fff', 
          padding: '1rem', 
          borderRadius: '18px 18px 0 0',
          textAlign: 'center'
        }}>
          <h2>🎤 Heirloom Speech Chat</h2>
        </div>

        {/* Chat Messages */}
        <div style={{ 
          flex: 1, 
          padding: '1rem', 
          overflowY: 'auto',
          display: 'flex',
          flexDirection: 'column',
          gap: '0.5rem'
        }}>
          {chatHistory.map((message, index) => (
            <div key={index} style={{
              maxWidth: '80%',
              padding: '0.75rem 1rem',
              borderRadius: 18,
              alignSelf: message.sender === 'User' ? 'flex-end' : 'flex-start',
              background: message.sender === 'User' ? '#e2c799' : 
                         message.sender === 'AI' ? '#fff' : '#f3e7d4',
              color: message.sender === 'User' ? '#5c4322' : '#8d6748',
              border: message.sender === 'AI' ? '1px solid #e2c799' : 'none',
              fontSize: '0.9rem'
            }}>
              {message.text}
            </div>
          ))}
          {isProcessing && (
            <div style={{
              alignSelf: 'center',
              padding: '0.5rem 1rem',
              background: '#f3e7d4',
              borderRadius: 12,
              color: '#8d6748',
              fontSize: '0.9rem'
            }}>
              ⏳ Processing your speech...
            </div>
          )}
        </div>

        {/* Recording Controls */}
        <div style={{ 
          padding: '1rem', 
          borderTop: '1px solid #e2c799',
          background: '#f7f3ef',
          borderRadius: '0 0 18px 18px',
          textAlign: 'center'
        }}>
          <button
            onClick={isRecording ? stopRecording : startRecording}
            disabled={isProcessing}
            style={{
              width: 60,
              height: 60,
              borderRadius: '50%',
              border: 'none',
              background: isRecording ? '#b08a60' : '#e2c799',
              color: isRecording ? '#fff' : '#8d6748',
              fontSize: '24px',
              cursor: isProcessing ? 'not-allowed' : 'pointer',
              transition: 'all 0.3s',
              animation: isRecording ? 'pulse 1.5s infinite' : 'none'
            }}
          >
            {isRecording ? '⏹️' : '🎤'}
          </button>
          <p style={{ marginTop: '0.5rem', color: '#8d6748', fontSize: '0.9rem' }}>
            {isRecording ? 'Recording... Click to stop' : 'Click to start recording'}
          </p>
        </div>
      </div>

      {/* Feedback Section */}
      <div style={{ 
        width: 320, 
        background: '#f3e7d4', 
        borderRadius: 20,
        display: 'flex',
        flexDirection: 'column',
        border: '2px solid #e2c799'
      }}>
        {/* Feedback Header */}
        <div style={{ 
          background: '#e2c799', 
          color: '#5c4322', 
          padding: '1rem', 
          borderRadius: '18px 18px 0 0',
          textAlign: 'center'
        }}>
          <h3>📊 Detailed Analysis</h3>
        </div>

        {/* Feedback Content */}
        <div style={{ 
          flex: 1, 
          padding: '1rem',
          display: 'flex',
          flexDirection: 'column'
        }}>
          <button
            onClick={requestDetailedFeedback}
            disabled={isLoadingFeedback || chatHistory.length === 0}
            style={{
              width: '100%',
              padding: '0.75rem',
              background: '#8d6748',
              color: '#fff',
              border: 'none',
              borderRadius: 8,
              cursor: isLoadingFeedback ? 'not-allowed' : 'pointer',
              fontWeight: 600,
              marginBottom: '1rem'
            }}
          >
            {isLoadingFeedback ? '⏳ Processing...' : 'Request Detailed Feedback'}
          </button>

          {feedback && (
            <div style={{
              background: '#fff',
              padding: '1rem',
              borderRadius: 8,
              border: '1px solid #e2c799',
              flex: 1,
              overflowY: 'auto',
              fontSize: '0.9rem',
              lineHeight: 1.5,
              whiteSpace: 'pre-wrap'
            }}>
              {feedback}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

function Admin() {
  const { user } = useUser();
  if (!user || user.role !== 'admin') return <Navigate to="/" />;
  return <div style={{ padding: '2rem' }}>Admin Page (Edit conversational features here)</div>;
}

function Login() {
  const { setUser } = useUser();

  const handleSuccess = async (credentialResponse) => {
    try {
      // Send the credential to your backend
      const response = await API.post('/auth/google/token', {
        credential: credentialResponse.credential
      });

      if (response.data.user) {
        setUser(response.data.user);
        window.location.href = '/';
      }
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  const handleError = () => {
    console.error('Login Failed');
  };

  return (
    <div style={{ padding: '4rem', textAlign: 'center' }}>
      <h2>Login with Google</h2>
      <GoogleLogin
        onSuccess={handleSuccess}
        onError={handleError}
        useOneTap
      />
    </div>
  );
}

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    API.get('/api/user').then(res => setUser(res.data)).catch(() => setUser(null));
  }, []);

  const logout = async () => {
    await API.get('/api/logout');
    setUser(null);
    window.location.href = '/';
  };

  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      <UserContext.Provider value={{ user, setUser, logout }}>
        <Router>
          <Navbar />
          <Routes>
            <Route path="/" element={<Landing />} />
            <Route path="/analyze" element={<Analyze />} />
            <Route path="/admin" element={<Admin />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </Router>
      </UserContext.Provider>
    </GoogleOAuthProvider>
  );
}

export default App;
