import React, { useEffect, useState, createContext, useContext, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from 'axios';

// Translucent backgrounds for sections
const translucentBg = 'rgba(60,76,115,0.06)'; // subtle accent tint
const translucentRose = 'rgba(195,141,148,0.08)'; // subtle rose tint

const UserContext = createContext();
const API = axios.create({ baseURL: 'http://localhost:3001', withCredentials: true });

function useUser() { return useContext(UserContext); }

function Logo() {
  // Placeholder logo: vintage key emoji
  return (
    <span style={{ fontSize: '2.2rem', marginRight: 12, verticalAlign: 'middle' }} role="img" aria-label="BeyondWords Key">🗝️</span>
  );
}

function Navbar() {
  const { user, logout } = useUser();
  return (
    <nav style={{ background: '#7e5a75', padding: '1rem 2rem', color: '#f5f1ec', display: 'flex', justifyContent: 'space-between', alignItems: 'center', boxShadow: '0 2px 8px #3c4c7333' }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Logo />
        <Link to="/" style={{ color: '#f5f1ec', fontWeight: 700, fontSize: '1.7rem', textDecoration: 'none', letterSpacing: 1 }}>BeyondWords Speech</Link>
      </div>
      <div>
        <Link to="/analyze" style={{ color: '#f5f1ec', marginRight: 20 }}>Analyze</Link>
        {user && user.role === 'admin' && <Link to="/admin" style={{ color: '#f5f1ec', marginRight: 20 }}>Admin</Link>}
        {user ? (
          <button onClick={logout} style={{ background: 'none', border: 'none', color: '#f5f1ec', cursor: 'pointer', fontSize: '1rem' }}>Logout</button>
        ) : (
          <Link to="/login" style={{ color: '#f5f1ec' }}>Login</Link>
        )}
      </div>
    </nav>
  );
}

function Landing() {
  // Waitlist state
  const [email, setEmail] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState('');

  const handleWaitlistSubmit = (e) => {
    e.preventDefault();
    setError('');
    if (!email.match(/^[^@\s]+@[^@\s]+\.[^@\s]+$/)) {
      setError('Please enter a valid email address.');
      return;
    }
    setSubmitted(true);
    setEmail('');
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: '#f5f1ec',
      fontFamily: 'Montserrat, Arial, sans-serif',
    fontWeight: 900,
      color: '#3e3e3e',
      padding: 0,
      margin: 0,
    }}>
      {/* Hero Section */}
      <div style={{
        display: 'flex',
        alignItems: 'stretch',
        justifyContent: 'flex-start',
        maxWidth: 1200,
        margin: '0 auto',
        padding: '5rem 2rem 2rem 2rem',
        gap: 64,
      }}>
        <div style={{ flex: 1, minWidth: 320, background: translucentBg, borderRadius: 24, boxShadow: '0 2px 12px #3c4c7322', padding: '2.5rem 2rem', display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
          <h1 style={{ fontSize: '2.8rem', fontWeight: 800, color: '#3c4c73', marginBottom: 18, lineHeight: 1.3 }}>Reconnect with Your Heritage Language</h1>
          <p style={{ fontSize: '1rem', color: '#3e3e3e', lineHeight: 1.9, marginBottom: 32, maxWidth: 500, fontFamily: 'AR One Sans', fontWeight: 400 }}>
            BeyondWords Speech is a gentle, AI-powered companion for heritage speakers—helping you rediscover, practice, and cherish your language in a safe, supportive space.
          </p>
          <div style={{ display: 'flex', gap: 16, marginBottom: 32 }}>
            <Link to="/analyze" style={{ background: '#7e5a75', color: '#fff', padding: '1rem 2.2rem', borderRadius: 12, fontFamily: 'AR One Sans', fontWeight: 700, textDecoration: 'none', fontSize: '1.1rem', boxShadow: '0 2px 8px #7e5a7533' }}>Start a Conversation</Link>
            <a href="#waitlist" style={{ background: '#c38d94', color: '#3e3e3e', padding: '1rem 2.2rem', borderRadius: 12, fontWeight: 700,fontFamily: 'AR One Sans', textDecoration: 'none', fontSize: '1.1rem', boxShadow: '0 2px 8px #c38d9433' }}>Join Waitlist</a>
          </div>
        </div>
        <div style={{ flex: 1, minWidth: 320, display: 'flex', alignItems: 'stretch' }}>
          <div style={{ background: translucentRose, borderRadius: 24, boxShadow: '0 2px 16px #3c4c7333', padding: '2.5rem 2rem', width: '100%', maxWidth: 400, textAlign: 'center', display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
            <Logo />
            <h2 style={{ color: '#7e5a75', fontSize: '2rem', margin: '0.5rem 0 1rem 0', fontFamily: 'Montserrat, Arial, sans-serif', fontWeight: 900, letterSpacing: 1 }}>BeyondWords Speech</h2>
            <form id="waitlist" onSubmit={handleWaitlistSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: 8 }}>
              {submitted ? (
                <div style={{ color: '#3e3e3e', background: '#c38d94', padding: '0.7rem 1.2rem', borderRadius: 10, fontWeight: 600, marginBottom: 8 }}>Thank you for joining the waitlist!</div>
              ) : (
                <>
                  <input
                    type="email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    placeholder="Your email address"
                    style={{
                      padding: '0.7rem 1rem',
                      borderRadius: 8,
                      border: '1.5px solid #c38d94',
                      marginBottom: 8,
