import React, { useEffect, useState, createContext, useContext, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from 'axios';

// Translucent backgrounds for sections
const translucentBg = 'rgba(60,76,115,0.06)'; // subtle accent tint
const translucentRose = 'rgba(195,141,148,0.08)'; // subtle rose tint

const UserContext = createContext();
const API = axios.create({ baseURL: 'http://localhost:3001', withCredentials: true });

function useUser() { return useContext(UserContext); }

function Logo() {
  // Placeholder logo: vintage key emoji
  return (
    <span style={{ fontSize: '2.2rem', marginRight: 12, verticalAlign: 'middle' }} role="img" aria-label="BeyondWords Key">üóùÔ∏è</span>
  );
}

function Navbar() {
  const { user, logout } = useUser();
  return (
    <nav style={{ 
      background: '#f5f1ec', 
      padding: '0.8rem 2rem', 
      color: '#3e3e3e', 
      display: 'flex', 
      justifyContent: 'space-between', 
      alignItems: 'center', 
      boxShadow: '0 4px 20px rgba(60,76,115,0.1)',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
      backdropFilter: 'blur(10px)',
      transition: 'all 0.3s ease'
    }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Logo />
        <Link to="/" style={{ 
          color: '#7e5a75', 
          fontWeight: 500, 
          fontSize: '1.5rem', 
          textDecoration: 'none', 
          letterSpacing: 1,
          fontFamily: 'Grandstander, Arial, sans-serif',
          transition: 'all 0.3s ease',
          ':hover': {
            transform: 'scale(1.05)',
            textShadow: '0 0 20px rgba(126,90,117,0.3)'
          }
        }}>BeyondWords Speech</Link>
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
        <Link to="/analyze" style={{ 
          color: '#7e5a75', 
          marginRight: 20,
          textDecoration: 'none',
          padding: '0.4rem 0.8rem',
          borderRadius: 8,
          transition: 'all 0.3s ease',
          ':hover': {
            background: 'rgba(126,90,117,0.1)',
            transform: 'translateY(-2px)'
          }
        }}>Analyze</Link>
        {user && user.role === 'admin' && <Link to="/admin" style={{ 
          color: '#7e5a75', 
          marginRight: 20,
          textDecoration: 'none',
          padding: '0.4rem 0.8rem',
          borderRadius: 8,
          transition: 'all 0.3s ease',
          ':hover': {
            background: 'rgba(126,90,117,0.1)',
            transform: 'translateY(-2px)'
          }
        }}>Admin</Link>}
        {user ? (
          <button onClick={logout} style={{ 
            background: 'rgba(126,90,117,0.1)', 
            border: '1px solid rgba(126,90,117,0.3)', 
            color: '#7e5a75', 
            cursor: 'pointer', 
            fontSize: '0.9rem',
            padding: '0.4rem 0.8rem',
            borderRadius: 8,
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(126,90,117,0.2)',
              transform: 'translateY(-2px)',
              boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
            }
          }}>Logout</button>
        ) : (
          <Link to="/login" style={{ 
            color: '#7e5a75',
            textDecoration: 'none',
            padding: '0.4rem 0.8rem',
            borderRadius: 8,
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(126,90,117,0.1)',
              transform: 'translateY(-2px)'
            }
          }}>Login</Link>
        )}
      </div>
    </nav>
  );
}

function Footer() {
  return (
    <footer style={{
      background: 'linear-gradient(135deg, #7e5a75 0%, #8a6a7a 25%, #a67a8a 75%, #c38d94 100%)',
      color: '#f5f1ec',
      padding: '3rem 2rem 2rem 2rem',
      marginTop: '4rem',
      textAlign: 'center',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Animated background elements */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'radial-gradient(circle at 20% 80%, rgba(245,241,236,0.1) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(195,141,148,0.1) 0%, transparent 50%)',
        animation: 'float 6s ease-in-out infinite'
      }}></div>

      <div style={{ position: 'relative', zIndex: 1 }}>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '1rem' }}>
          <Logo />
          <span style={{ 
            fontSize: '1.5rem', 
            fontWeight: 500, 
            marginLeft: '0.5rem',
            letterSpacing: 1,
            fontFamily: 'Grandstander, Arial, sans-serif'
          }}>BeyondWords Speech</span>
        </div>

        <p style={{ 
          fontSize: '1rem', 
          marginBottom: '2rem', 
          opacity: 0.9,
          maxWidth: '600px',
          margin: '0 auto 2rem auto',
          lineHeight: 1.6
        }}>
          Reconnecting heritage speakers with their language roots through gentle, AI-powered conversation.
        </p>

        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          gap: '2rem', 
          marginBottom: '2rem',
          flexWrap: 'wrap'
        }}>
          <a href="#privacy" style={{ 
            color: '#f5f1ec', 
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            borderRadius: 6,
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(245,241,236,0.1)',
              transform: 'translateY(-2px)'
            }
          }}>Privacy Policy</a>
          <a href="#terms" style={{ 
            color: '#f5f1ec', 
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            borderRadius: 6,
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(245,241,236,0.1)',
              transform: 'translateY(-2px)'
            }
          }}>Terms of Service</a>
          <a href="#contact" style={{ 
            color: '#f5f1ec', 
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            borderRadius: 6,
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(245,241,236,0.1)',
              transform: 'translateY(-2px)'
            }
          }}>Contact Us</a>
        </div>

        <div style={{ 
          borderTop: '1px solid rgba(245,241,236,0.2)', 
          paddingTop: '1.5rem',
          fontSize: '0.9rem',
          opacity: 0.8
        }}>
          ¬© 2024 BeyondWords Speech. Made with ‚ù§Ô∏è for heritage speakers everywhere.
        </div>
      </div>
    </footer>
  );
}

function Landing() {
  // Waitlist state
  const [email, setEmail] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState('');

  const handleWaitlistSubmit = (e) => {
    e.preventDefault();
    setError('');
    if (!email.match(/^[^@\s]+@[^@\s]+\.[^@\s]+$/)) {
      setError('Please enter a valid email address.');
      return;
    }
    setSubmitted(true);
    setEmail('');
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: '#f5f1ec',
      fontFamily: 'Montserrat, Arial, sans-serif',
      fontWeight: 900,
      color: '#3e3e3e',
      padding: 0,
      margin: 0,
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Animated Background Swirls */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        pointerEvents: 'none',
        zIndex: 0
      }}>
        {/* Swirl 1 */}
        <div style={{
          position: 'absolute',
          top: '10%',
          left: '5%',
          width: '120px',
          height: '120px',
          border: '3px solid rgba(126,90,117,0.1)',
          borderTop: '3px solid rgba(126,90,117,0.3)',
          borderRadius: '50%',
          animation: 'swirl 8s linear infinite',
          transform: 'rotate(0deg)'
        }}></div>

        {/* Swirl 2 */}
        <div style={{
          position: 'absolute',
          top: '30%',
          right: '10%',
          width: '80px',
          height: '80px',
          border: '2px solid rgba(195,141,148,0.1)',
          borderRight: '2px solid rgba(195,141,148,0.3)',
          borderRadius: '50%',
          animation: 'swirl 12s linear infinite reverse',
          transform: 'rotate(45deg)'
        }}></div>

        {/* Swirl 3 */}
        <div style={{
          position: 'absolute',
          bottom: '20%',
          left: '15%',
          width: '100px',
          height: '100px',
          border: '2px solid rgba(60,76,115,0.1)',
          borderBottom: '2px solid rgba(60,76,115,0.3)',
          borderRadius: '50%',
          animation: 'swirl 10s linear infinite',
          transform: 'rotate(90deg)'
        }}></div>

        {/* Swirl 4 */}
        <div style={{
          position: 'absolute',
          top: '60%',
          right: '20%',
          width: '60px',
          height: '60px',
          border: '2px solid rgba(126,90,117,0.1)',
          borderLeft: '2px solid rgba(126,90,117,0.3)',
          borderRadius: '50%',
          animation: 'swirl 15s linear infinite reverse',
          transform: 'rotate(180deg)'
        }}></div>

        {/* Floating dots */}
        <div style={{
          position: 'absolute',
          top: '15%',
          right: '30%',
          width: '8px',
          height: '8px',
          background: 'rgba(195,141,148,0.4)',
          borderRadius: '50%',
          animation: 'float 6s ease-in-out infinite'
        }}></div>

        <div style={{
          position: 'absolute',
          bottom: '40%',
          left: '25%',
          width: '6px',
          height: '6px',
          background: 'rgba(126,90,117,0.4)',
          borderRadius: '50%',
          animation: 'float 8s ease-in-out infinite 2s'
        }}></div>

        <div style={{
          position: 'absolute',
          top: '80%',
          right: '5%',
          width: '10px',
          height: '10px',
          background: 'rgba(60,76,115,0.4)',
          borderRadius: '50%',
          animation: 'float 7s ease-in-out infinite 1s'
        }}></div>
      </div>

      {/* Content with higher z-index */}
      <div style={{ position: 'relative', zIndex: 1 }}>
        {/* Hero Section */}
        <div style={{
          display: 'flex',
          alignItems: 'stretch',
          justifyContent: 'center',
          margin: '0 auto',
          padding: '5rem 2rem 2rem 2rem',
          gap: 64,
        }}>
          <div style={{ 
            flex: 1, 
            minWidth: 320, 
            background: translucentBg, 
            borderRadius: 24, 
            boxShadow: '0 2px 12px #3c4c7322', 
            padding: '2.5rem 2rem', 
            display: 'flex', 
            flexDirection: 'column', 
            justifyContent: 'center',
            transition: 'all 0.3s ease',
            ':hover': {
              transform: 'translateY(-5px)',
              boxShadow: '0 8px 25px #3c4c7333'
            }
          }}>
            <h1 style={{ fontSize: '2.8rem', fontWeight: 600, color: '#3c4c73', marginBottom: 18, lineHeight: 1.3, fontFamily: 'Grandstander, Arial, sans-serif' }}>Reconnect with Your Heritage Language</h1>
            <p style={{ fontSize: '1rem', color: '#3e3e3e', lineHeight: 1.9, marginBottom: 32, maxWidth: 500, fontFamily: 'AR One Sans', fontWeight: 400 }}>
              BeyondWords Speech is a gentle, AI-powered companion for heritage speakers‚Äîhelping you rediscover, practice, and cherish your language in a safe, supportive space.
            </p>
            <div style={{ display: 'flex', gap: 16, marginBottom: 32 }}>
              <Link to="/analyze" style={{ 
                background: '#7e5a75', 
                color: '#fff', 
                padding: '1rem 2.2rem', 
                borderRadius: 12, 
                fontFamily: 'AR One Sans', 
                fontWeight: 700, 
                textDecoration: 'none', 
                fontSize: '1.1rem', 
                boxShadow: '0 2px 8px #7e5a7533',
                transition: 'all 0.3s ease',
                ':hover': {
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 15px #7e5a7555'
                }
              }}>Start a Conversation</Link>
              <a href="#waitlist" style={{ 
                background: '#c38d94', 
                color: '#3e3e3e', 
                padding: '1rem 2.2rem', 
                borderRadius: 12, 
                fontWeight: 700,
                fontFamily: 'AR One Sans', 
                textDecoration: 'none', 
                fontSize: '1.1rem', 
                boxShadow: '0 2px 8px #c38d9433',
                transition: 'all 0.3s ease',
                ':hover': {
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 15px #c38d9455'
                }
              }}>Join Waitlist</a>
            </div>
          </div>
          <div style={{ flex: 1, minWidth: 320, display: 'flex', alignItems: 'stretch' }}>
            <div style={{ 
              background: translucentRose, 
              borderRadius: 24, 
              boxShadow: '0 2px 16px #3c4c7333', 
              padding: '2.5rem 2rem', 
              width: '100%', 
              maxWidth: 400, 
              textAlign: 'center', 
              display: 'flex', 
              flexDirection: 'column', 
              justifyContent: 'center',
              transition: 'all 0.3s ease',
              ':hover': {
                transform: 'translateY(-5px)',
                boxShadow: '0 8px 25px #3c4c7333'
              }
            }}>
              <Logo />
              <h2 style={{ color: '#7e5a75', fontSize: '2rem', margin: '0.5rem 0 1rem 0', fontFamily: 'Grandstander, Arial, sans-serif', fontWeight: 700, letterSpacing: 1 }}>BeyondWords Speech</h2>
              <form id="waitlist" onSubmit={handleWaitlistSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: 8 }}>
                {submitted ? (
                  <div style={{ color: '#3e3e3e', background: '#c38d94', padding: '0.7rem 1.2rem', borderRadius: 10, fontWeight: 600, marginBottom: 8 }}>Thank you for joining the waitlist!</div>
                ) : (
                  <>
                    <input
                      type="email"
                      value={email}
                      onChange={e => setEmail(e.target.value)}
                      placeholder="Your email address"
                      style={{
                        padding: '0.7rem 1rem',
                        borderRadius: 8,
                        border: '1.5px solid #c38d94',
                        marginBottom: 25,
                        marginTop: 6,
                        width: 220,
                        fontSize: '1rem',
                        fontFamily: 'AR One Sans',
                        background: '#f5f1ec',
                        color: '#3e3e3e',
                      }}
                      required
                    />
                    <button type="submit" style={{
                      background: '#7e5a75',
                      color: '#fff',
                      border: 'none',
                      borderRadius: 8,
                      padding: '0.7rem 2.7rem',
                      fontWeight: 600,
                      fontSize: '1rem',
                      cursor: 'pointer',
                      boxShadow: '0 2px 8px #7e5a7522',
                      marginBottom: 4,
                    }}>Notify Me</button>
                  </>
                )}
                {error && <div style={{ color: '#b04a2b', fontSize: '0.95rem', marginTop: 2 }}>{error}</div>}
              </form>
            </div>
          </div>
        </div>

        {/* Features Row */}
        <div style={{ display: 'flex', justifyContent: 'center', gap: 32, margin: '0 auto', marginTop: 32, flexWrap: 'wrap', background: translucentBg, borderRadius: 24, boxShadow: '0 2px 12px #3c4c7322', padding: '2.5rem 2rem' }}>
          <FeatureCard icon="ü§ù" title="Conversational Practice" desc="Chat naturally with AI to build confidence and fluency in your heritage language." noBorder />
          <FeatureCard icon="üå±" title="Gentle Guidance" desc="Supportive, non-judgmental feedback to help you grow at your own pace." noBorder />
          <FeatureCard icon="üóùÔ∏è" title="Cultural Connection" desc="Rediscover stories, sayings, and traditions that make your language unique." noBorder />
        </div>

        {/* Stats/Feature Block */}
        <div style={{ display: 'flex', justifyContent: 'center', gap: 32, margin: '48px 0 0 0', flexWrap: 'wrap', background: translucentRose, borderRadius: 24, boxShadow: '0 2px 12px #3c4c7322', padding: '2.5rem 2rem' }}>
          <FeatureCard icon={null} title="1000+ Heritage Speakers Helped" desc="" noBorder bg="#7e5a75" textColor="#fff" />
          <FeatureCard icon={null} title="100% Free to Start" desc="" noBorder bg="#c38d94" textColor="#fff" />
        </div>

        {/* Testimonials Section */}
        <div style={{ background: translucentBg, borderRadius: 18, margin: '48px 0 0 0', padding: '2.2rem 2rem', width: '100%', boxShadow: '0 1px 8px #3c4c7322', textAlign: 'center', fontFamily: 'AR One Sans' }}>
          <h2 style={{ color: '#7e5a75', fontSize: '1.4rem', marginBottom: 18, fontWeight: 500, fontFamily: 'Grandstander, Arial, sans-serif' }}>What Heritage Speakers Say</h2>
          <Testimonial quote="I always wanted to speak with my grandmother in her language. BeyondWords Speech made me feel comfortable practicing, even when I made mistakes." name="‚Äî Sofia, Spanish Heritage Speaker" />
          <Testimonial quote="It feels like having a patient friend who understands my journey. I'm finally reconnecting with my roots." name="‚Äî Daniel, Vietnamese Heritage Speaker" />
